import { environment } from 'src/environments/environment';
export class CoreService {
  constructor(client, path) {
    this.client = client;
    this.apiPath = '/' + path + '/';
    this.apiUrl = environment.api + this.apiPath;
  }

  post(model) {
    return this.client.post(this.apiUrl, model);
  }

  postWithUrl(path, model) {
    const api = this.apiUrl + `${path}`;
    return this.client.post(api, model);
  }

  putWithUrl(path, model) {
    const api = this.apiUrl + `${path}`;
    return this.client.put(api, model);
  }

  put(model, id) {
    return this.client.put(this.apiUrl + id, model);
  }

  get(search) {
    return this.client.get(this.apiUrl + search);
  }

  delete(path) {
    return this.client.delete(this.apiUrl + path);
  }

}
